<?php require_once dirname(__FILE__)."/class.single.php";class Pages extends Single {	public function __construct($xmltag = "single", $elements =		Array(			Array('wysiwyg', Array( 'id' => 'article', 'name' => 'Contenu')),			Array('gallery', Array( 'id' => 'background', 'name' => 'Background')),			Array('text', Array( 'id' => 'meta-title', 'name' => 'Meta-title')),			Array('text', Array( 'id' => 'meta-description', 'name' => 'Meta-description')),			Array('text', Array( 'id' => 'meta-keywords', 'name' => 'Meta-keywords'))		)	) {    $this->setXMLTag('page');    $this->setfileName($xmltag);    $this->setElements($elements);		$this->setUploadPath("../ups/htm/");		$lang = 'fr'; if(isset($_SESSION['lang'])) $lang = $_SESSION['lang'];		$this->setPath(dirname(dirname(__FILE__)).'/xml/'.$lang.'/htm/'.$this->getfileName().'.xml');		if(file_exists( $this -> getPath() ))		{			$context  = stream_context_create(array('http' => array('header' => 'Accept: application/xml')));			$xml = file_get_contents($this ->  getPath(), false, $context);			if ( substr_count ( $xml , '<' ) < 2 )				$this->setXML( new SimpleXMLElement('<page></page>') );			else				$this->setXML( simplexml_load_string($xml, NULL, LIBXML_NOCDATA) );		} else			$this->setXML( new SimpleXMLElement('<'.$this->xmltag().'></'.$this->xmltag().'>'));    $xml = $this->getXML();    $xmlChildren = $xml->children();		foreach($elements as $element)			if(!property_exists($xmlChildren,classify($element[1]['name'])))				$xml -> addChild(classify($element[1]['name']));	}}?>